
def characterReplacement( s: str, k: int) -> int:
        if not s or k < 0:
            return 0
        def long_str(right, k , s):
            ch = s[right]
            curr_len = 0
            while right < len(s):
                print(s[right])
                if s[right] != ch:
                    if k <= 0:
                        break
                    k -= 1
                right += 1
                curr_len += 1
            if k > 0:
                curr_len = min(len(s), curr_len+k)
            return curr_len
        max_len = 0
        for i in range(len(s)):
            if i == 0 or s[i] != s[i - 1]:
                max_len = max(max_len, long_str(i, k ,s))
        
        return max_len

def main():
    arr = ""
    k = 4567
    result = characterReplacement(arr, k)
    print(result)

main()